puts "Combien d'handle contiennent un numéro ? "

def number_of_username_with_numeral
# création d'une fonction pour obtenir la réponse en 1 seule ligne dans le terminal
	total_numeral_username = 0 
	# notre décompte du nombre de noms d'utilisateur avec numéro commence à zéro
	@listing_username.each do |username| 
		# pour chaque valeur du tableau listing_username faire :
		which_index = 0 
		# déterminer quelle valeur du tableau on va analyser via son numéro d'index
			while which_index < username.length
			# Boucle: tant que le numéro d'index appelé est inférieur ou égal
			# au nombre de valeurs contenues dans mon tableau, faire :
				if ("0".."9").cover?(username[which_index])
				# si dans la valeur appelée (ici une chaine de caractère ciblée 
				# grâce à son numéro d'index) on trouve l'un des caractères
				# demandé de cette gamme (ici "0" à "9"), alors :
					total_numeral_username += 1
					# ajouter + 1 à mon total de nom d'utilisateur avec un numéro
					break
					# ce break permet de stopper l'analyse de la valeur en cours
					# afin de ne pas compter deux fois le même nom d'utilisateur
				end
				# fin de la condition if
				which_index += 1
				# on incrémente d'1 le numéro d'index afin d'analyser la valeur 
				# suivante du tableau
			end
			# fin de la boucle while
		end
		# fin de la boucle each do
	return total_numeral_username
	# résultat retourné pour la fonction
end

print " La solution est --> "
puts number_of_username_with_numeral # affiche le résultat de la question

