puts "Combien d'handle contiennent les 4 lettres du prénom 'Aude' à la suite (sans faire attention à la casse) ? "

def number_of_username_with_aude
	total_username_with_aude = 0
	@listing_username.each do |username|
		username = username.downcase
		# on met toutes les lettres du nom d'utilisateur en minuscule
			if username.include?("aude")
			# si le nom d'utilisateur contient la chaine de caractère "aude" alors:
				total_username_with_aude += 1
				# ajouter + 1 au total du nombre d'utilisateur avec majuscule au début
			end
		end
	return total_username_with_aude
end

print " La solution est --> "
puts number_of_username_with_aude

puts "-" * 20


puts "Combien commencent par une majuscule ?"

def number_of_username_with_first_upcase
	total_username_with_first_upcase = 0
	@listing_username.each do |username|
		first_letter = 1
		# on prend en compte la première lettre du nom d'utilisateur,
		# 0 étant le @, on met 1
		if ("A".."Z").cover?(username[first_letter])
		# si la 1ere lettre du nom d'utilisateur est contenue dans la gamme 
		# alphabet en masjucule, alors: 
			total_username_with_first_upcase += 1
			# ajouter 1 au total du nombre d'utilisateur avec majuscule au début
		end
	end
	return total_username_with_first_upcase
end

print " La solution est --> "
puts number_of_username_with_first_upcase

puts "-" * 20

puts "Combien contiennent une majuscule ?"

def number_of_username_with_upcase
	total_username_with_upcase = 0
	@listing_username.each do |username|
		which_index = 0
		# déterminer quelle valeur du tableau on va analyser via son numéro d'index
		while which_index < username.length
			if ("A".."Z").cover?(username[which_index])
			# si le nom d'utilisateur contient une lettre de la gamme 
			# alphabet en masjucule, alors: 
				total_username_with_upcase += 1
				# ajouter 1 au total du nombre d'utilisateur avec majuscule
				break
			end
			which_index += 1
		end
	end
	return total_username_with_upcase
end

print " La solution est --> "
puts number_of_username_with_upcase

